//
// Author:       Brian Fromme
// Copyright:    Copyright 2020, Gnu Public License - Version 2.0
// Credits:      Brian Fromme, Bryan Gartner, Darren Soothill
// Maintainer:   Brian Fromme
// Status:       Prototype
// Description:  Sonar form component - leveraged from grommet-sandbox
//

import React, { useState } from "react";
import { FormClose } from 'grommet-icons';
import {
    Box,
    Button,
    CheckBox,
    Collapsible,
    Form,
    FormField,
    Grommet,
    Heading,
    Layer,
    RadioButton,
    RadioButtonGroup,
    RangeInput,
    ResponsiveContext,
    Select,
    Text,
    TextArea,
} from 'grommet';

//
// The form UI
//

// Simple True/False selector
function TrueFalse() {
    const [value, setValue] = React.useState('true');
    return (
      <Select
          options={['true', 'false']}
          value={value}
          onChange={({ option }) => setValue(option)}
        />
    );
}


// Simple UseCase selector
function UseCase(props) {
    const [value, setValue] = React.useState('Archive');
    return (
      <Select
          options={['Archive', 'Mixed']}
          value={value}
          onChange={({ option }) => setValue(option)}
          {...props}
        />
    );
}

// OutputMessage area
function OutputMessage(props) {
    return (
        <Box direction="column" pad="small"> <Text> </Text>
            <Box direction="row" border={{ color: 'brand', size: 'small' }} background="light-3" pad="small">
              <Text
                  weight="bold"
                  truncate={true}
                  margin={{"vertical":"0px", "horizontal":"0px", "top":"0px", "bottom":"0px", "left":"0px", "right":"10px"}}>
                  {props.desc}
              </Text>
              <Text
                  color="status-critical"
                  margin={{"vertical":"0px", "horizontal":"0px", "top":"0px", "bottom":"0px", "left":"10px", "right":"0px"}}
                 >
                  output messages...
              </Text>
            </Box>
        </Box>
    );
}

// NOTE: This hack manages placement of "desc" property passed to InputFormField.  Change for various fonts, etc.
// {"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}
//
// Simple Numeric Range selector
const NumericRange = (props) => (
    <Box direction="row">
      <Text truncate={true} margin={{"vertical":"20px", "horizontal":"0px", "top":"4px", "bottom":"6px", "left":"0px", "right":"10px"}}>
          {props.desc}
      </Text>
      <RangeInput
        onChange={event => this.setValue(event.target.value)}
        {...props}
      />
    </Box>
);

// onChange={event => this.setState({ value: event.target.value })}
// onChange={({ option }) => setValue(option)}


// NOTE: This hack manages placement of "desc" property passed to InputFormField.  Change for various fonts, etc.
// {"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}
//
// Specific FormField definition
const InputFormField = (props) => (
    <Box direction="row">
      <Text margin={{"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}}>
          {props.desc}
      </Text>
      <FormField
        direction='row-responsive'
        {...props}
      />
    </Box>
);

// Labels
const LABEL_COLO = "Co-Location (GW/MDS/MON): ";
const LABEL_USECASE = "Use Case: ";
const LABEL_CAPREQ = "Capacity Required (TB): ";
const LABEL_METACAP = "Metadata Capacity (TB): ";
const LABEL_DRIVECAP = "Drive Capacity (TB): ";
const LABEL_DRIVESPER = "# of Drives / Chassis: ";
const LABEL_SLOTSPER = "# of Populated Slots / Chassis: ";
const LABEL_NVMEPER = "# of NVMe Slots / Chassis: ";
const LABEL_DRIVETYPES = "Drive Types: ";
const LABEL_MAXFILLCAP = "Maximum Fill Capacity %: ";
const LABEL_NMVERATIO = "NVMe Ratio (1:n): ";
const LABEL_PROTTYPE = "Protection Type: ";
const LABEL_ECDATA = "Error Correction - Data: ";
const LABEL_ECPARITY = "Error Correction - Parity: ";
const LABEL_MESSAGES = "Messages: ";

function FormComponent(props) {
  // This defines the form contents
  return (
    <Form onSubmit={({ value }) => console.log("Submit", value)}>
      <InputFormField desc={LABEL_COLO} name="colo" component={TrueFalse} />
      <InputFormField desc={LABEL_USECASE} name="usecase" component={UseCase} />

      <NumericRange desc={LABEL_CAPREQ} name="capreq" component={UseCase} />
      <NumericRange desc={LABEL_METACAP} name="metacap" component={UseCase} />
      <NumericRange desc={LABEL_DRIVECAP} name="drivecap" component={UseCase} />
      <NumericRange desc={LABEL_DRIVESPER} name="drivesper" component={UseCase} />
      <NumericRange desc={LABEL_SLOTSPER} name="slotsper" component={UseCase} />
      <NumericRange desc={LABEL_NVMEPER} name="nvmeper" component={UseCase} />
      <NumericRange desc={LABEL_DRIVETYPES} name="drivetypes" component={UseCase} />
      <NumericRange desc={LABEL_MAXFILLCAP} name="maxfillcap" component={UseCase} />
      <NumericRange desc={LABEL_NMVERATIO} name="nvmeratio" component={UseCase} />
      <NumericRange desc={LABEL_PROTTYPE} name="prottype" component={UseCase} />
      <NumericRange desc={LABEL_ECDATA} name="ecdata" component={UseCase} />
      <NumericRange desc={LABEL_ECPARITY} name="ecparity" component={UseCase} />

      <OutputMessage desc={LABEL_MESSAGES} name="output" />

      <Box direction="row" justify="between" margin={{ top: "medium" }}>
          <Button label="Cancel" />
          <Button type="submit" label="Update" primary />
      </Box>
    </Form>
  );
}

export default FormComponent;
