//
// Author:       Brian Fromme
// Copyright:    Copyright 2020, Gnu Public License - Version 2.0
// Credits:      Brian Fromme, Bryan Gartner, Darren Soothill
// Maintainer:   Brian Fromme
// Status:       Prototype
// Description:  Sonar main module
//

import React, { useState } from "react";
import { FormClose, Notification } from 'grommet-icons';
import {
  Box,
  Button,
  Collapsible,
  Heading,
  Grommet,
  Layer,
  ResponsiveContext,
} from 'grommet';

// Definitions
// 
const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '24px',
      height: '30px',
    },
  },
};

//
// The User Interface
//

const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);


function App() {
  const [showSidebar, setShowSidebar] = useState(false);
    return (
    <Grommet theme={theme} themeMode="dark">
    <ResponsiveContext.Consumer>
       {size => (
       <Box fill>
         <AppBar>
           <Heading level='3' margin='none'>My App</Heading>
           <Button
             icon={<Notification />}
             onClick={() => setShowSidebar(!showSidebar)}
           />
         </AppBar>
         <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
           <Box flex align='center' justify='center'>
             app body
           </Box>
           {(!showSidebar || size !== 'small') ? (
             <Collapsible direction="horizontal" open={showSidebar}>
               <Box
                 flex
                 width='medium'
                 background='light-2'
                 elevation='small'
                 align='center'
                 justify='center'
               >
                 sidebar
               </Box>
             </Collapsible>
           ): (
             <Layer>
               <Box
                 background='light-2'
                 tag='header'
                 justify='end'
                 align='center'
                 direction='row'
               >
                 <Button
                   icon={<FormClose />}
                   onClick={() => setShowSidebar(false)}
                 />
               </Box>
               <Box
                 fill
                 background='light-2'
                 align='center'
                 justify='center'
               >
                 sidebar
               </Box>
             </Layer>
           )}
         </Box>
       </Box>
       )}
      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

// <Box align="center" background="neutral-2">
//   <Button label="hello world" primary onClick={() => alert('hello, world')} />
// </Box>

export default App;
