import React from 'react';
import { Box, Grommet } from 'grommet';

const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '24px',
      height: '30px',
    },
  },
};

const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);


function App() {
  return (
    <Grommet theme={theme}>
      <AppBar>Welcome to Grommet!</AppBar>
    </Grommet>
  );
}

export default App;
