//
// Author:       Brian Fromme
// Copyright:    Copyright 2020, Gnu Public License - Version 2.0
// Credits:      Brian Fromme, Bryan Gartner, Darren Soothill
// Maintainer:   Brian Fromme
// Status:       Prototype
// Description:  Sonar main module
//

import React, { useState } from "react";
import { FormClose, Hpe } from 'grommet-icons';
import {
    Box,
    Button,
    CheckBox,
    Collapsible,
    Form,
    FormField,
    Grommet,
    Heading,
    Layer,
    RadioButton,
    RangeInput,
    ResponsiveContext,
    Select,
    TextArea,
} from 'grommet';

//
// Definitions
// 
const theme = {
    global: {
        font: {
            family: 'Roboto',
            size: '24px',
            height: '30px',
        },
    },
};

//
// The User Interface
//

const AppName = "Sonar Sizing Tool";

const AppBar = (props) => (
    <Box
        tag='header'
        direction='row'
        align='center'
        justify='between'
        background='brand'
        pad={{ left: 'medium', right: 'small', vertical: 'small' }}
        elevation='medium'
        style={{ zIndex: '1' }}
        {...props}
    />
);


//
// The form UI
//

const RadioButtonGroup = ({ name, onChange, options, value }) => (
  <Box gap="small">
    {options.map(option => (
      <Box key={option}>
        <RadioButton
          name={name}
          value={option}
          label={option}
          checked={value === option}
          onChange={() => onChange({ value: option })}
        />
      </Box>
    ))}
  </Box>
);

const DemoForm = () => (
  <Form onSubmit={({ value }) => console.log("Submit", value)}>
    <FormField
      label="Name"
      name="name"
      required
      validate={{ regexp: /^[a-z]/i }}
    />
    <FormField label="Email" name="email" type="email" required />
    <FormField
      label="Employee ID"
      name="employeeId"
      required
      validate={{ regexp: /^[0-9]{4,6}$/, message: "4-6 digits" }}
    />
    <FormField
      name="subscribe"
      component={CheckBox}
      pad
      label="Subscribe?"
    />
    <FormField
      name="ampm"
      component={RadioButtonGroup}
      pad
      options={["morning", "evening"]}
    />
    <FormField
      label="Size"
      name="size"
      component={Select}
      options={["small", "medium", "large", "xlarge"]}
    />
    <FormField label="Comments" name="comments" component={TextArea} />
    <FormField
      label="Age"
      name="age"
      component={RangeInput}
      pad
      min={15}
      max={75}
    />
    <Box direction="row" justify="between" margin={{ top: "medium" }}>
      <Button label="Cancel" />
      <Button type="submit" label="Update" primary />
    </Box>
  </Form>
);


function App() {
  const [showSidebar, setShowSidebar] = useState(false);
    return (
    <Grommet theme={theme} themeMode="dark">
    <ResponsiveContext.Consumer>
       {size => (
       <Box fill>
         <AppBar>
           <Heading level='3' margin='none'>{AppName}</Heading>
           <Hpe />
           <Button
             icon={<Update />}
             onClick={() => setShowSidebar(!showSidebar)}
           />
         </AppBar>
         <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
           <Box flex align='center' justify='center'>
             sizing parameters
           </Box>
           {(!showSidebar || size !== 'small') ? (
             <Collapsible direction="horizontal" open={showSidebar}>
               <Box
                 flex
                 width='medium'
                 background='light-2'
                 elevation='small'
                 align='center'
                 justify='center'
               >
                 sizing results
               </Box>
             </Collapsible>
           ): (
             <Layer>
               <Box
                 background='light-2'
                 tag='header'
                 justify='end'
                 align='center'
                 direction='row'
               >
                 <Button
                   icon={<FormClose />}
                   onClick={() => setShowSidebar(false)}
                 />
               </Box>
               <Box
                 fill
                 background='light-2'
                 align='center'
                 justify='center'
               >
                 sidebar
               </Box>
             </Layer>
           )}
         </Box>
       </Box>
       )}
      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

export default App;
