//
// Author:       Brian Fromme
// Copyright:    Copyright 2020, Gnu Public License - Version 2.0
// Credits:      Brian Fromme, Bryan Gartner, Darren Soothill
// Maintainer:   Brian Fromme
// Status:       Prototype
// Description:  Sonar form component - leveraged from grommet-sandbox
//

import React, { useState } from "react";
import { FormClose } from 'grommet-icons';
import {
    Box,
    Button,
    CheckBox,
    Collapsible,
    Form,
    FormField,
    Grommet,
    Heading,
    Layer,
    RadioButton,
    RadioButtonGroup,
    RangeInput,
    ResponsiveContext,
    Select,
    Text,
    TextArea,
} from 'grommet';

//
// The form UI
//

// Simple True/False selector
function TrueFalse() {
    const [value, setValue] = React.useState('true');
    return (
      <Select
          options={['true', 'false']}
          value={value}
          onChange={({ option }) => setValue(option)}
        />
    );
}


// Simple UseCase selector
function UseCase() {
    const [value, setValue] = React.useState('Archive');
    return (
      <Select
          options={['Archive', 'Mixed']}
          value={value}
          onChange={({ option }) => setValue(option)}
        />
    );
}

// {"vertical":"6px", "horizontal":"0px", "top":"0px", "bottom":"0px", "left":"0px", "right":"6px"}

// Specific FormField definition
const InputFormField = (props) => (
    <Box direction="row">
      <Text margin={{"vertical":"20px", "horizontal":"16px", "top":"0px", "bottom":"0px", "left":"0px", "right":"6px"}}>
            Help!</Text>
    <FormField
        direction='row-responsive'
        {...props}
    />
    </Box>
);

// align='start'
// height='medium'
// justify='between'
// background='brand'
// elevation='none'
// pad={{ left: 'medium', right: 'small', vertical: 'small' }}

// Labels
const LABEL_COLO = "Co-Location of Gateway/MDS/MONs: ";
const LABEL_USECASE = "Use case: ";

function FormComponent(props) {
  // This defines the form contents
  return (
    <Form onSubmit={({ value }) => console.log("Submit", value)}>
      <InputFormField name="colo" component={TrueFalse} />
      <InputFormField label={LABEL_COLO} name="colo" component={TrueFalse} />
      <InputFormField label={LABEL_USECASE} name="usecase" component={UseCase} />

      <Box direction="row" justify="between" margin={{ top: "medium" }}>
          <Button label="Cancel" />
          <Button type="submit" label="Update" primary />
      </Box>
    </Form>
  );
}

export default FormComponent;
