//
// Author:       Brian Fromme
// Copyright:    Copyright 2020, Gnu Public License - Version 2.0
// Credits:      Brian Fromme, Bryan Gartner, Darren Soothill
// Maintainer:   Brian Fromme
// Status:       Prototype
// Description:  Sonar form component - leveraged from grommet-sandbox
//

import React from "react";
import {
    Box,
    Button,
    Form,
    FormField,
    RangeInput,
    Select,
    Text,
} from 'grommet';

//
// The form UI
//

// Simple True/False selector
function TrueFalse() {
    const [value, setValue] = React.useState('true');
    return (
      <Select
          options={['true', 'false']}
          value={value}
          onChange={({ option }) => setValue(option)}
        />
    );
}


// UI data
const USECASE_ARCHIVE = 'Archive';
const USECASE_MIXED = 'Mixed';

// Simple UseCase selector
function UseCase(props) {
    const [value, setValue] = React.useState({USECASE_ARCHIVE});
    return (
      <Select
          options={[{USECASE_ARCHIVE}, {USECASE_MIXED}]}
          value={USECASE_ARCHIVE}
          onChange={({ option }) => setValue(option)}
          {...props}
        />
    );
}

// OutputMessage area
function OutputMessage(props) {
    return (
        <Box direction="column" pad="small"> <Text> </Text>
            <Box direction="row" border={{ color: 'brand', size: 'small' }} background="light-3" pad="small">
              <Text
                  weight="bold"
                  truncate={true}
                  margin={{"vertical":"0px", "horizontal":"0px", "top":"0px", "bottom":"0px", "left":"0px", "right":"10px"}}>
                  {props.desc}
              </Text>
              <Text
                  color="status-critical"
                  margin={{"vertical":"0px", "horizontal":"10px", "top":"0px", "bottom":"0px", "left":"10px", "right":"0px"}}
                 >
                  output messages...
              </Text>
            </Box>
        </Box>
    );
}

// Display an error or warning message
function outputErrorOrWarning(message, err) {
    // Look up the OutputMessage object by ID
    var obj = document.getElementById(ID_MESSAGES);
    var outputMess;
    if (err) {
        // preceed with: Error:
        outputMess = "Error: " + message;
    }
    else {
        // preceed with: Warning:
        outputMess = "Warning: " + message;
    }
    // TBD: set the value portion of the Text area
    alert(outputMess);
}

// NOTE: This hack manages placement of "desc" property passed to InputFormField.  Change for various fonts, etc.
// {"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}
//

class NumericRange extends React.Component {
  constructor(props) {
      super(props);
      this.state = {value: 0};
      var nrDescription = props.desc;
      var nrProperties = props;

      this.onChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    alert('A value was changed: ' + this.state.value);
    this.setState({value: event.target.value});
  }

  render() {
    return (
        <Box direction="row">
          <Text truncate={true} margin={{"vertical":"20px", "horizontal":"0px", "top":"4px", "bottom":"6px", "left":"0px", "right":"10px"}}>
              {this.nrDescription}
          </Text>
          <RangeInput {...this.nrProperties} />
        </Box>
    );
  }
}

// Simple Numeric Range selector
// const NumericRange = (props) => (

//function numericRangeChange = id => (event) => {
function numericRangeChange (event) {
    // This is the callback for the RangeInput components.
    // id should be the name of the 
    var newval = event.target.value;
    var obj = event.target;
    var id = event.target.name;
    ////var id = event.target.tagName;
    // sorry, I didn't want to do this
    switch(id) {
      case ID_CAPREQ:
        // code block
        break;
      case ID_METACAP:
        // code block
        break;
      case ID_DRIVECAP:
        // code block
        break;
      case ID_DRIVESPER:
        // code block
        break;
      case ID_SLOTSPER:
        // code block
        break;
      case ID_NVMEPER:
        // code block
        break;
      case ID_MAXFILLCAP:
        // code block
        break;
      case ID_NMVERATIO:
        // code block
        break;
      default:
        // code block
    }
}


// onChange={event => this.setState({ value: event.target.value })}
// onChange={({ option }) => setValue(option)}


// NOTE: This hack manages placement of "desc" property passed to InputFormField.  Change for various fonts, etc.
// {"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}
//
// Specific FormField definition
const InputFormField = (props) => (
    <Box direction="row">
      <Text margin={{"vertical":"20px", "horizontal":"0px", "top":"7px", "bottom":"0px", "left":"0px", "right":"10px"}}>
          {props.desc}
      </Text>
      <FormField
        direction='row-responsive'
        {...props}
      />
    </Box>
);

// UI object IDs
const ID_COLO = "colo";
const ID_USECASE = "usecase";
const ID_CAPREQ = "capreq";
const ID_METACAP = "metacap";
const ID_DRIVECAP = "drivecap";
const ID_DRIVESPER = "drivesper";
const ID_SLOTSPER = "slotsper";
const ID_NVMEPER = "nvmeper";
const ID_DRIVETYPES = "drivetypes";
const ID_MAXFILLCAP = "maxfillcap";
const ID_NMVERATIO = "nvmeratio";
const ID_PROTTYPE = "prottype";
const ID_ECDATA = "ecdata";
const ID_ECPARITY = "ecparity";
const ID_MESSAGES = "output";

// Labels
const LABEL_COLO = "Co-Location (GW/MDS/MON): ";
const LABEL_USECASE = "Use Case: ";
const LABEL_CAPREQ = "Capacity Required (TB): ";
const LABEL_METACAP = "Metadata Capacity (TB): ";
const LABEL_DRIVECAP = "Drive Capacity (TB): ";
const LABEL_DRIVESPER = "# of Drives / Chassis: ";
const LABEL_SLOTSPER = "# of Populated Slots / Chassis: ";
const LABEL_NVMEPER = "# of NVMe Slots / Chassis: ";
const LABEL_DRIVETYPES = "Drive Types: ";
const LABEL_MAXFILLCAP = "Maximum Fill Capacity %: ";
const LABEL_NMVERATIO = "NVMe Ratio (1:n): ";
const LABEL_PROTTYPE = "Protection Type: ";
const LABEL_ECDATA = "Error Correction - Data: ";
const LABEL_ECPARITY = "Error Correction - Parity: ";
const LABEL_MESSAGES = "Messages: ";

function FormComponent(props) {
  // This defines the form contents
  return (
    <Form onSubmit={({ value }) => console.log("Submit", value)}>
      <InputFormField desc={LABEL_COLO} name={ID_COLO} component={TrueFalse} />
      <InputFormField desc={LABEL_USECASE} name={ID_USECASE} component={UseCase} />

      <NumericRange desc={LABEL_CAPREQ} name={ID_CAPREQ} component={UseCase} />
      <NumericRange desc={LABEL_METACAP} name={ID_METACAP} component={UseCase} />
      <NumericRange desc={LABEL_DRIVECAP} name={ID_DRIVECAP} component={UseCase} />
      <NumericRange desc={LABEL_DRIVESPER} name={ID_DRIVESPER} component={UseCase} />
      <NumericRange desc={LABEL_SLOTSPER} name={ID_SLOTSPER} component={UseCase} />
      <NumericRange desc={LABEL_NVMEPER} name={ID_NVMEPER} component={UseCase} />
      <NumericRange desc={LABEL_DRIVETYPES} name={ID_DRIVETYPES} component={UseCase} />
      <NumericRange desc={LABEL_MAXFILLCAP} name={ID_MAXFILLCAP} component={UseCase} />
      <NumericRange desc={LABEL_NMVERATIO} name={ID_NMVERATIO} component={UseCase} />
      <NumericRange desc={LABEL_PROTTYPE} name={ID_PROTTYPE} component={UseCase} />
      <NumericRange desc={LABEL_ECDATA} name={ID_ECDATA} component={UseCase} />
      <NumericRange desc={LABEL_ECPARITY} name={ID_ECPARITY} component={UseCase} />

      <OutputMessage desc={LABEL_MESSAGES} name={ID_MESSAGES} />

      <Box direction="row" justify="between" margin={{ top: "medium" }}>
          <Button label="Cancel" />
          <Button type="submit" label="Update" primary />
      </Box>
    </Form>
  );
}

export default FormComponent;
